mapper.select:
SELECT t1.post_id, t1.title, t1.content, t1.create_time, t1.update_time, t1.browse_number, t1.like_number, t1.collection_number, t1.del_flag, t1.recommend, t2.user_id, t2.nick_name, t2.avatar, t4.remark, t4.audit_status, t1.node FROM post t1 JOIN user t2 ON t1.user_id = t2.user_id LEFT JOIN tags_post tp ON t1.post_id = tp.post_id Left Join post_audit t4 ON t1.post_id = t4.post_id
AND t1.post_id = #{postId}
AND t1.user_id = #{userId}
AND t1.del_flag = #{delFlag}
AND t1.recommend = #{recommend}
and t4.audit_status = #{auditStatus}
AND t1.title LIKE CONCAT('%', #{title}, '%')
GROUP BY t1.post_id, t1.title, t1.content, t1.create_time, t1.update_time, t1.browse_number, t1.like_number, t1.collection_number, t1.del_flag, t1.recommend, t1.node, t2.user_id, t2.nick_name, t2.avatar, t4.remark, t4.audit_status ORDER BY t1.update_time DESC,t1.create_time DESC
